mapper.select:
SELECT BM_NO,BM_TITLE,BM_CONTENT,BM_WRITER,BM_DATE,BM_HIT,USER_IDNO,BM_IMG 
FROM (SELECT ROWNUM NUM,N.* 
		FROM (SELECT BM_NO,BM_TITLE,BM_CONTENT,BM_WRITER,BM_DATE,BM_HIT,USER_IDNO,BM_IMG 
FROM BIZ_MGZ
ORDER BY BM_NO)N

)WHERE NUM BETWEEN #{param1} AND #{param2}

SELECT BM_TITLE 
FROM BIZ_MGZ
WHERE BM_NO=#{param1}

mapper.update:
UPDATE BIZ_MGZ SET
BM_TITLE=#{param2},BM_CONTENT=#{param3}
WHERE BM_NO=#{param1}
